import React, {useState} from 'react';
import './NewExpense.css';
import ExpenseForm from './ExpenseForm';

const NewExpense = (props)=>{
    const [isEditing, setIsEditing] = useState(false);
    const saveExpenseDataHandler = (enteredExpenseData) =>{
        const expenseData ={
            ...enteredExpenseData,
            id: Math.random().toString()
        };
        props.addNewExpense(expenseData);
        setIsEditing(false);
    }
    const changeEditingHandler =()=>{
        setIsEditing(true);
    }
    const cancelEditingHandler=()=>{
        setIsEditing(false);
    }
    return <div className='new-expense'>
        {!isEditing && <button onClick={changeEditingHandler}>Add New Expense</button>}
        {isEditing && <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onCancel={cancelEditingHandler}/>}
    </div>
}

export default NewExpense;
